import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router'

import store from '../../store'
import { fetchUniquePost, selectPosts } from './postSlice'

function BlogDetail() {
    const { id } = useParams()

    useEffect(() => {
        store.dispatch(fetchUniquePost(id))
    }, [])

    //const post = useSelector(selectPosts())

    const post = { "_id": "62d2afcc7515204b5e5a6bd6", "title": "second post", "content": "Content", "hidden": true, "pinned": false, "tags": ["bob", "bob"], "postTime": "date", "author": "Mohamed Chamrouk" }

    console.log(post)

    const { image, tags, title, content, postTime, author } = post

    if ([tags, title, content, postTime, author].map((item) => typeof item === undefined).includes(true)) {
        return (
            <>
            </>
        )
    }

    const renderedImage = image !== undefined ? <img className="rounded-lg flex w-full bg-auto justify-start mr-7 pb-7" src={image} /> : <></>

    return (
        <>
            <div className="flex justify-start w-full">
                <div className="mt-3 mb-3 flex flex-col w-full">
                    <a className="font-bold text-3xl pb-2 pt-7" href="#">{title}</a>
                    <p className="font-normal text-sm pb-6"><span className="font-medium">{tags}</span> &#8226; {postTime}</p>
                    {renderedImage}
                    <p className="font-normal text-gray-700 text-lg pb-3">{content}</p>
                    <p className="font-medium flex justify-end flex-1 items-end text-sm">{author}</p>
                </div>
            </div>
        </>
    )
}

export default BlogDetail
