import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'
import { useParams } from 'react-router'
import { Link } from 'react-router-dom'
import ReactMarkdown from 'react-markdown'

import { IoReturnDownBackOutline } from 'react-icons/io5'

import store from '../../store'
import { fetchUniquePost, selectPosts } from './postSlice'

function BlogDetail() {
    const { id } = useParams()

    console.log(id)

    useEffect(() => {
        store.dispatch(fetchUniquePost(id))
    }, [])

    let post = useSelector(selectPosts)
    console.log(post)

    post = post[0]
    //TODO : clean this up to not have to select out of an array

    const placeHolderPost = { "_id": "62d2afcc7515204b5e5a6bd6", "title": "Post loading...", "content": "", "hidden": true, "pinned": false, "tags": [""], "postTime": "", "author": "" }

    const { image, tags, title, content, postTime, author } = post === undefined ? placeHolderPost : post

    if ([tags, title, content, postTime, author].map((item) => typeof item === undefined).includes(true)) {
        return (
            <>
            </>
        )
    }

    const renderedImage = image !== undefined ? <img className="rounded-lg flex w-full bg-auto justify-start mr-7 pb-7" src={image} /> : <></>

    return (
        <>
            <div className="flex justify-start w-full">
                <div className="mt-3 mb-3 flex flex-col w-full">
                    <div className="flex flex-row justify-start items-center pb-2 pt-7"><Link to="/" ><span className="rounded-full px-2 sm:px-4 pb-1 mr-1 bg-gray-200 font-bold text-xl sm:text-3xl ">&#8249;</span></Link> <p className="font-bold text-xl sm:text-3xl">{title}</p></div>
                    <p className="font-normal text-sm pb-6"><span className="font-medium">{tags}</span> &#8226; {postTime}</p>
                    {renderedImage}
                    <ReactMarkdown className="font-normal text-gray-700 text-base sm:text-lg pb-3">{content}</ReactMarkdown>
                    <p className="font-medium flex justify-end flex-1 items-end text-sm">{author}</p>
                </div>
            </div>
        </>
    )
}

export default BlogDetail
