function SinglePost(props) {
    const {image, tags, title, content, date, author} = props

    const contentSummary = content.slice(0, 250)

    const renderedImage = image !== undefined ? <img className="rounded-lg flex w-full bg-auto justify-start mr-7" src={image} /> : <></>

    return (
        <div className="pb-7">
            { renderedImage }
            <div className="mt-3 mb-3 flex flex-col">
                <p className="font-normal text-sm pb-2"><span className="font-medium">{tags}</span> &#8226; {date}</p>
                <a className="font-bold text-3xl pb-5" href="#">{title}</a>
                <p className="font-normal text-gray-700 text-lg pb-3">{contentSummary}</p>
                <p className="font-medium flex justify-end flex-1 items-end text-sm">{author}</p>
            </div>
        </div>
    )
}

export default SinglePost
