import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'

import SinglePost from './SinglePost'
import PinnedPost from './PinnedPost'

import store from '../../store'
import { fetchPosts, selectPosts } from './blogSlice'

function BlogPosts() {
    useEffect(() => {
        store.dispatch(fetchPosts())
    }, [])

    const posts = useSelector(selectPosts)
    let renderedPostsLeft, renderPostsRight

    posts.forEach((post, index) => {
        if (index%2 === 0) {
            renderedPostsLeft.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.date} author={post.author}  />)
        } else {
            renderPostsRight.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.date} author={post.author}  />)
        }
    }) 

    return (
        <>
            {/* Welcome banner */}
            <div className="w-full bg-[url('./plus.svg')] flex items-start flex-col bg-top mt-3 mb-5 pt-3 pb-7">
                <p className="ml-7 font-sans text-3xl text-left font-bold">Bienvenue sur mon <span className="text-cyan-500">blog</span> !</p>
                <p className="ml-7 ">Mon blog personnel, centré essentiellement sur le développement web.</p>
            </div>

            {/* Recent posts */}
            <div className="grid grid-cols-2 gap-7">
                <div className="flex flex-col justify-center col-span-1">
                </div>
                <div className="flex flex-col justify-start col-span-1">
                </div>
            </div>
        </>
    )
}

export default BlogPosts
