import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'

import SinglePost from './SinglePost'

import store from '../../store'
import { fetchPosts, selectPosts } from './blogSlice'
import { AiFillPushpin } from 'react-icons/ai'

function BlogPosts() {
    useEffect(() => {
        store.dispatch(fetchPosts())
    }, [])

    const posts = useSelector(selectPosts)
    let renderedPostsLeft = [], renderPostsRight = [], renderPinnedPost = []

    console.log(posts)

    if (posts[0] !== undefined) {
        posts[0].forEach((post, index) => {
            console.log('les posts :')
            console.log(post)
            if (index % 2 === 0) {
                renderedPostsLeft.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} />)
            } else {
                renderPostsRight.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} />)
            }

            if (post.pinned) {
                renderPinnedPost.push(
                    <div className="mb-7 pb-[100px] ">
                        <div className="flex flex-row items-center mb-3"><AiFillPushpin className="mr-1" /> <p className="text-base font-bold">POSTS EPINGLE</p></div>
                        <div className="flex justify-center">
                            <SinglePost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} />
                        </div>
                    </div>
                )
            }
        })
    }

    return (
        <>
            {/* Welcome banner */}
            <div className="w-full bg-[url('./plus.svg')] flex items-start flex-col bg-top mt-3 mb-5 pt-3 pb-7">
                <p className="ml-7 font-sans text-3xl text-left font-bold">Bienvenue sur mon <span className="text-cyan-500">blog</span> !</p>
                <p className="ml-7 ">Mon blog personnel, centré essentiellement sur le développement web.</p>
            </div>

            {renderPinnedPost}

            {/* Recent posts */}
            <div className="grid grid-cols-2 gap-7">
                <div className="flex flex-col justify-center col-span-1">
                    {renderedPostsLeft}
                </div>
                <div className="flex flex-col justify-start col-span-1">
                    {renderPostsRight}
                </div>
            </div>
        </>
    )
}

export default BlogPosts
