import React from 'react'

function BlogDetail(props) {
    const {image, tags, title, content, date, author} = props

    if ([tags, title, content, date, author].map((item) => typeof item === undefined).includes(true)) {
        return(
            <>
            </>
        )
    }

    const renderedImage = image !== undefined ? <img className="rounded-lg flex w-full bg-auto justify-start mr-7 pb-7" src={image} /> : <></>

    return (
        <>
            <div className="mb-7 pb-[100px]">
                <div className="flex justify-center">
                    <div className="mt-3 mb-3 flex flex-col">
                        <a className="font-bold text-3xl pb-2 pt-7" href="#">{title}</a>
                        <p className="font-normal text-sm pb-6"><span className="font-medium">{tags}</span> &#8226; {date}</p>
                        {renderedImage}
                        <p className="font-normal text-gray-700 text-lg pb-3">{content}</p>
                        <p className="font-medium flex justify-end flex-1 items-end text-sm">{author}</p>
                    </div>
                </div>
            </div>
        </>
    )
}

export default BlogDetail
