import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'

import SinglePost from './SinglePost'
import PinnedPost from './PinnedPost'

import store from '../../store'
import { fetchPosts, selectBlog } from './blogSlice'

function BlogPosts() {
    useEffect(() => {
        store.dispatch(fetchPosts())
    }, [])

    const posts = useSelector(selectBlog)
    let renderedPostsLeft = [], renderPostsRight = [], renderPinnedPost = []


    if (posts[0] !== undefined) {
        posts[0].forEach((post, index) => {
            if (index % 2 === 0) {
                renderedPostsLeft.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} key={post._id} id={post._id} />)
            } else {
                renderPostsRight.push(<SinglePost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} key={post._id} id={post._id} />)
            }

            if (post.pinned) {
                renderPinnedPost.push(
                    <PinnedPost title={post.title} tags={post.tags} content={post.content} date={post.postTime} author={post.author} key={post._id} />
                )
            }
        })
    }

    return (
        <>
            <div>
                {/* Welcome banner */}
                <div className="w-full bg-[url('./plus.svg')] flex items-start flex-col bg-top mt-3 mb-5 pt-1 pb-4 sm:pt-3 sm:pb-7">
                    <p className="ml-2 mr-2 font-sans text-xl text-left font-bold sm:text-3xl sm:ml-7 sm:mr-7">Bienvenue sur mon <span className="text-cyan-500">blog</span> !</p>
                    <p className="ml-2 mr-2 text-sm sm:text-base sm:ml-7 sm:mr-7">Mon blog personnel, centré essentiellement sur le développement web.</p>
                </div>

                {renderPinnedPost}

                {/* Recent posts */}
                <div className="grid grid-cols-2 gap-7">
                    <div className="flex flex-col justify-center col-span-1">
                        {renderedPostsLeft}
                    </div>
                    <div className="flex flex-col justify-start col-span-1">
                        {renderPostsRight}
                    </div>
                </div>
            </div>
        </>
    )
}

export default BlogPosts
